import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import { Table, Divider, Tag } from 'antd';
import axios from 'axios';
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
  MDBInput,
} from "mdbreact";
import { Tabs, Tab, Form, Row, Col  } from 'react-bootstrap';
import DatePicker from "react-datepicker"; 
import "react-datepicker/dist/react-datepicker.css";
import './ModalForm.css'
class dvHome extends React.Component {
  constructor(props) {
    super(props);
    this.editDetails = this.editDetails.bind(this);
    this.approveApplicaion = this.approveApplicaion.bind(this);
    this.rejectApplicaion = this.rejectApplicaion.bind(this);
  }
  state = {
    modal2: false,
  };

  toggle = nr => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber]
    });
  };
  editDetails(e) {
    console.log(e.target.id);
    axios({
    method: 'POST',
    url: 'http://localhost:9090/application-processing/dvEditLoanDetails',
    //contentType: "application/json",
    data: e.target.id,
    //config: { headers: {'Content-Type': 'application/json' }}}
    })
    .then(function (response) {
        //handle success
        window.alert("success");
        toggle(2);
        window.location.reload();
        //window.location.assign("http://localhost:3000/form3?ID="+response.data);
    })
    .catch(function (response) {
        //handle error
        console.log(response);
    });

  }
  rejectApplicaion(e) {
    console.log(e.target.id);
    axios({
    method: 'POST',
    url: 'http://localhost:9090/application-processing/dvRejectedLoans',
    //contentType: "application/json",
    data: e.target.id,
    //config: { headers: {'Content-Type': 'application/json' }}}
    })
    .then(function (response) {
        //handle success
        window.alert("success");
        window.location.reload();
        //window.location.assign("http://localhost:3000/form3?ID="+response.data);
    })
    .catch(function (response) {
        //handle error
        console.log(response);
    });

  }
  
  approveApplicaion(e) {
    console.log(e.target.id);
    axios({
    method: 'POST',
    url: 'http://localhost:9090/application-processing/dvApprovedLoans',
    //contentType: "application/json",
    data: e.target.id,
    //config: { headers: {'Content-Type': 'application/json' }}}
    })
    .then(function (response) {
        //handle success
        window.alert("success");
        window.location.reload();
        //window.location.assign("http://localhost:3000/form3?ID="+response.data);
    })
    .catch(function (response) {
        //handle error
        console.log(response);
    });

  }
  
  componentDidMount() {
    axios.get(`http://localhost:9090/application-processing/dvRecievedApplications`)
      .then(res => {
        // const posts = res.data.data.children.map(obj => obj.data);
        //console.log(res.data);
        var temp1 = res.data;
        if(temp1){
        for(var i=0;i<temp1["rows"].length;i++){
          for(var j=0;j<temp1["button"].length;j++){
            var iddata = temp1["rows"][i][temp1["button"][j]["key"]];
            var action = temp1["rows"][i][temp1["button"][j]["action"]]+i.toString();
            
            var button1 = <span>
              <button type={"button"} id={iddata} onClick={this.editDetails}>Edit</button>&nbsp;&nbsp;&nbsp;
              <button type={"button"} id={iddata} onClick={this.approveApplicaion}>Aprrove</button>&nbsp;&nbsp;&nbsp;
              <button type={"button"} id={iddata} onClick={this.rejectApplicaion}>Reject</button>
            </span>;
            temp1["rows"][i][temp1["button"][j]["key"]] = button1;
          }
        this.setState(temp1);
        //console.log(res.data);
        }
        }
      });
  }

render(){
  const header={display:'flex', justifyContent:'center', backgroundColor:'#f5f5f5' }
  //console.log(this.state);
  const columns = this.state['columns'];
  const data = this.state['rows'];
return (
<Table columns={columns} dataSource={data}/>
);
} }         
export default dvHome;
