import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import { Table, Divider, Tag } from 'antd';
import axios from 'axios';

class managerHome extends React.Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.handleClickReject = this.handleClickReject.bind(this);
  }
  state = {};

  handleClickReject(e) {
    console.log(e.target.id);
    axios({
    method: 'POST',
    url: 'http://localhost:9090/application-processing/managerRejectedLoans',
    //contentType: "application/json",
    data: e.target.id,
    //config: { headers: {'Content-Type': 'application/json' }}}
    })
    .then(function (response) {
        //handle success
        window.alert("success");
        window.location.reload();
        //window.location.assign("http://localhost:3000/form3?ID="+response.data);
    })
    .catch(function (response) {
        //handle error
        console.log(response);
    });

  }
  
  handleClick(e) {
    console.log(e.target.id);
    axios({
    method: 'POST',
    url: 'http://localhost:9090/application-processing/managerApprovedLoans',
    //contentType: "application/json",
    data: e.target.id,
    //config: { headers: {'Content-Type': 'application/json' }}}
    })
    .then(function (response) {
        //handle success
        window.alert("success");
        window.location.reload();
        //window.location.assign("http://localhost:3000/form3?ID="+response.data);
    })
    .catch(function (response) {
        //handle error
        console.log(response);
    });

  }
  
  componentDidMount() {
    axios.get(`http://localhost:9090/application-processing/managerRecievedApplications`)
      .then(res => {
        // const posts = res.data.data.children.map(obj => obj.data);
        console.log(res.data);
        var temp1 = res.data;
        if(temp1.length){
        for(var i=0;i<temp1["rows"].length;i++){
          for(var j=0;j<temp1["button"].length;j++){
            var iddata = temp1["rows"][i][temp1["button"][j]["key"]];
            var action = temp1["rows"][i][temp1["button"][j]["action"]]+i.toString();
            var title = temp1["rows"][i][temp1["button"][j]["title"]];
            
          var button1 = <span>
        <button type={"button"} id={iddata} onClick={this.editDetails}>E</button>
        <button type={"button"} id={iddata} onClick={this.handleClick}>A</button>
        <button type={"button"} id={iddata} onClick={this.handleClickReject}>R</button>
        </span>;
        temp1["rows"][i][temp1["button"][j]["key"]] = button1;
        }
        }
        this.setState(temp1);
        console.log(res.data);
      }
      });
  }

render(){
  console.log(this.state);
  const columns = this.state['columns'];
  const data = this.state['rows'];
return (
<Table columns={columns} dataSource={data}/>
);
} }         
export default managerHome;
