import React from 'react';
//import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import {Table, Divider, Tag} from 'antd';
import axios from 'axios';

class staffHome extends React.Component {
  constructor(props) {
    super(props);
    this.approveApplicaion = this.approveApplicaion.bind(this);
    this.rejectApplicaion = this.rejectApplicaion.bind(this);
  }
  state = {};

  rejectApplicaion(e) {
    console.log(e.target.id);
    axios({
    method: 'POST',
    url: 'http://localhost:9090/application-processing/staffRejectedLoans',
    //contentType: "application/json",
    data: e.target.id,
    //config: { headers: {'Content-Type': 'application/json' }}}
    })
    .then(function (response) {
        //handle success
        window.alert("success");
        window.location.reload();
        //window.location.assign("http://localhost:3000/form3?ID="+response.data);
    })
    .catch(function (response) {
        //handle error
        console.log(response);
    });

  }
  
  approveApplicaion(e) {
    console.log(e.target.id);
    axios({
    method: 'POST',
    url: 'http://localhost:9090/application-processing/staffApprovedLoans',
    //contentType: "application/json",
    data: e.target.id,
    //config: { headers: {'Content-Type': 'application/json' }}}
    })
    .then(function (response) {
        //handle success
        window.alert("success");
        window.location.reload();
        //window.location.assign("http://localhost:3000/form3?ID="+response.data);
    })
    .catch(function (response) {
        //handle error
        console.log(response);
    });

  }
  
  componentDidMount() {
    axios.get(`http://localhost:9090/application-processing/staffRecievedApplications`)
      .then(res => {
        // const posts = res.data.data.children.map(obj => obj.data);
        console.log(res.data);
        var temp1 = res.data;
        for(var i=0;i<temp1["rows"].length;i++){
          for(var j=0;j<temp1["button"].length;j++){
            var iddata = temp1["rows"][i][temp1["button"][j]["key"]];
            var action = temp1["rows"][i][temp1["button"][j]["action"]]+i.toString();
            var title = temp1["button"][j]["title"];[i][temp1["button"][j]["title"]];
            
          var button1 = <span>
        <button type={"button"} id={iddata} onClick={this.approveApplicaion}>A</button>
        <button type={"button"} id={iddata} onClick={this.rejectApplicaion}>R</button>
        </span>;
        temp1["rows"][i][temp1["button"][j]["key"]] = button1;
        }
        this.setState(temp1);
        console.log(res.data);
      }
      });
  }

render(){
  console.log(this.state);
  const columns = this.state['columns'];
  const data = this.state['rows'];
return (
<Table columns={columns} dataSource={data}/>
);
} }         
export default staffHome;
