import React, { Component } from 'react';
import axios from 'axios';
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from 'mdbreact';
import DocsLink from './../components/docsLink';
import SectionContainer from './../components/sectionContainer';
import DatePicker from "react-datepicker"; 
import "react-datepicker/dist/react-datepicker.css";
var getParams = function (url) {
        var params = {};
        var parser = document.createElement('a');
        parser.href = url;
        var query = parser.search.substring(1);
        var vars = query.split('&');
        for (var i = 0; i < vars.length; i++) {
          var pair = vars[i].split('=');
          params[pair[0]] = decodeURIComponent(pair[1]);
        }
        return params;
};
class fourthForm extends Component {
  //var par= getParams(this.props.location)
  state = {
    fullname: '',
    POB:'',
    gender:'',
    startDate: '',
    religion:'',
    martialStatus:'',
    ApplicantId : this.props.location

  };

  submitHandler = event => {
    
    axios({
    method: 'POST',
    url: 'http://localhost:9090/application-form/received',
    contentType: "application/json",
    data: {...this.state},
    //config: { headers: {'Content-Type': 'application/json' }}}
    })
    .then(function (response) {
        //handle success
        //window.alert(response);
        console.log(response);
        window.alert(response.data);
    })
    .catch(function (response) {
        //handle error
        console.log(response);
    });
    event.preventDefault();
    event.target.className += ' was-validated';
  };

  changeHandler = event => {
    this.setState({ ...this.state, [event.target.name]: event.target.value});
  };
  handleChange = date => {
    this.setState({
      startDate: date
    });
  };

  render() {
    return (
      <MDBContainer>
        <DocsLink
          title='Form'
          href='https://mdbootstrap.com/docs/react/forms/validation/'
        />
        <SectionContainer header='Validations'>
          <form
            className='needs-validation'
            onSubmit={this.submitHandler}
            noValidate
          >
            
              <MDBCol md='4' className='mb-3'>
                <label
                  htmlFor='defaultFormRegisterNameEx'
                  className='grey-text'
                >
                  Full name
                </label>
                <input
                  value={this.state.fullname}
                  name='fullname'
                  onChange={this.changeHandler}
                  type='text'
                  id='defaultFormRegisterNameEx'
                  className='form-control'
                  placeholder='Full name'
                  required
                />
                <div className='valid-feedback'>Looks good!</div>
              </MDBCol>
              <MDBCol md='4' className='mb-3'>
                <label
                  htmlFor='defaultFormRegisterName'
                  className='grey-text'
                >
                  Place of Birth
                </label>
                <input
                  value={this.state.POB}
                  name='POB'
                  onChange={this.changeHandler}
                  type='text'
                  id='defaultFormRegisterName'
                  className='form-control'
                  placeholder='place of birth'
                  required
                />
                <div className='valid-feedback'>Looks good!</div>
              </MDBCol>
              <MDBCol md='4' className='mb-3'>
              <label
                  className='grey-text'
                >
                  Date Of Birth
                </label>
               
              <DatePicker className='form-control'
                selected={this.state.startDate}
                onChange={this.handleChange}
                />
            </MDBCol>
                
              <MDBCol md='4' className='mb-3'>
              <label 
                  className='grey-text'
                >
                  Gender
                </label>
              <div className='custom-control custom-radio'>
                <input
                 checked={this.state.gender === "male"}
                 value="male"
                 onChange={this.changeHandler}
                  type='radio'
                  className='custom-control-input'
                  id='customControlValidation2'
                  name='gender'
                  required
                />
                <label
                  className='custom-control-label'
                  htmlFor='customControlValidation2'
                >
                  male
                </label>
              </div>
              <div className='custom-control custom-radio mb-3'>
                <input
                checked={this.state.gender === "female"}
                value="female"
                 onChange={this.changeHandler}
                  type='radio'
                  className='custom-control-input'
                  id='customControlValidation3'
                  name='gender'
                  required
                />
                <label
                  className='custom-control-label'
                  htmlFor='customControlValidation3'
                >
                  female
                </label>
                <div className='invalid-feedback'>
                  Select any radio button
                </div>
              </div>
              </MDBCol>
              <MDBCol md='4' className='mb-3'>
              <label
                  className='grey-text'
                >
                  Religion
                </label>
              <div className='form-group'>
                <select onChange={this.changeHandler} name='religion' value={this.state.religion} className='custom-select bMDBRowser-default' required>
                  <option value=''>Choose Religion</option>
                  <option value='hindu'>Hindu</option>
                  <option value='muslim'>Muslim</option>
                  <option value='sikh'>sikh</option>
                </select>
                <div className='invalid-feedback'>
                  Select one
                </div>
              </div>
              </MDBCol>
              <MDBCol md='4' className='mb-3'>
              <label
                  className='grey-text'
                >
                  Martial Status
                </label>
              <div className='form-group'>
                <select onChange={this.changeHandler} name='martialStatus' value={this.state.martialStatus} className='custom-select bMDBRowser-default' required>
                  <option value=''>Choose Martial Status</option>
                  <option value='single'>Single</option>
                  <option value='married'>Married</option>
                  <option value='divorced'>Divorced</option>
                </select>
                <div className='invalid-feedback'>
                  Select one
                </div>
              </div>
              </MDBCol>
            
            <MDBBtn color='unique' type='submit'>
              Submit Form
            </MDBBtn>
          </form>
        </SectionContainer>
      </MDBContainer>
    );
  }
}

export default fourthForm;
